//*****************************************************************************
//FILE NAME: therm_latch.v
//OWNER    : NIKHIL KUMAR SRIVASTAVA
//COMMENTS : This module latches the values generated by the therm_decoder so
//           that the interpolator inputs are stable during the interpolation 
//*****************************************************************************

                                                                                module therm_latch(clk1, clk1_b, clk2, clk2_b, t_latchin_s1, t_latchout_s1 );

input clk1, clk1_b, clk2, clk2_b;
input [7:0] t_latchin_s1;

output [7:0] t_latchout_s1;

reg [7:0] t_temp_s2, t_latchout_s1;


always @(clk1 or  t_latchin_s1)
  if(clk1)
    t_temp_s2 = t_latchin_s1;

always @(clk2 or t_temp_s2 )
  if(clk2)
    t_latchout_s1 = t_temp_s2;

endmodule // end therm_latch module
